import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    // localStorage'dan güvenli veri okuma fonksiyonu
    const getRolesFromStorage = () => {
        try {
            const roleData = localStorage.getItem("role");
            console.log("Raw role data:", roleData, "Type:", typeof roleData);

            if (!roleData || roleData === "undefined" || roleData === "null") {
                return [];
            }

            // Eğer zaten bir array ise direkt döndür
            if (Array.isArray(roleData)) {
                return roleData;
            }

            // String ise parse et
            const parsed = JSON.parse(roleData);
            return Array.isArray(parsed) ? parsed : [];

        } catch (error) {
            console.error("localStorage role verisi bozuk:", error);
            console.log("Bozuk veri temizleniyor...");
            localStorage.removeItem("role");
            return [];
        }
    };

    // Component mount olduğunda zaten giriş yapılmış mı kontrol et
    useEffect(() => {
        console.log("Login component mounted, checking existing roles...");

        try {
            const roles = getRolesFromStorage();
            console.log("Found roles:", roles);

            if (roles && roles.length > 0 && roles[0] && roles[0].authority) {
                const role = roles[0].authority;
                console.log("Redirecting based on role:", role);

                if (role === "PROJE_YONETICISI") {
                    navigate("/admin-dashboard", { replace: true });
                } else if (role === "CALISAN") {
                    navigate("/user-dashboard", { replace: true });
                }
            }
        } catch (error) {
            console.error("useEffect error:", error);
            localStorage.clear();
        }
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setLoading(true);

        try {
            console.log("Attempting login...");

            // Backend'e login isteği gönder
            const response = await axios.post("http://localhost:8080/api/login", {
                username,
                password,
            });

            console.log("Login response:", response.data);

            // Backend'den kullanıcı bilgisi ve rol geliyor
            const userData = response.data;

            if (userData && userData.roles && userData.roles.length > 0) {
                console.log("Saving roles to localStorage:", userData.roles);

                // Rol bilgisini localStorage'a kaydet - string olarak
                localStorage.setItem("role", JSON.stringify(userData.roles));
                localStorage.setItem("username", username);

                // Rol bazlı yönlendirme (ilk rolü alıyoruz)
                const role = userData.roles[0].authority;
                console.log("Redirecting to dashboard for role:", role);

                if (role === "PROJE_YONETICISI") {
                    navigate("/admin-dashboard", { replace: true });
                } else if (role === "CALISAN") {
                    navigate("/user-dashboard", { replace: true });
                } else {
                    setError("Geçersiz rol tipi: " + role);
                }
            } else {
                setError("Kullanıcı rol bilgisi alınamadı.");
            }
        } catch (err) {
            console.error("Login error:", err);
            if (err.response) {
                setError(`Giriş başarısız: ${err.response.data.message || 'Sunucu hatası'}`);
            } else if (err.request) {
                setError("Sunucuya bağlanılamıyor. Lütfen bağlantınızı kontrol edin.");
            } else {
                setError("Bir hata oluştu. Lütfen tekrar deneyin.");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
            <h2>Giriş Yap</h2>

            {/* Debug bilgisi - geliştirme aşamasında */}
            <div style={{ fontSize: "12px", color: "#666", marginBottom: "10px" }}>
                localStorage role: {localStorage.getItem("role") || "yok"}
            </div>

            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: "10px" }}>
                    <input
                        placeholder="Kullanıcı Adı"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        disabled={loading}
                        style={{ width: "100%", padding: "8px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <input
                        placeholder="Şifre"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        disabled={loading}
                        style={{ width: "100%", padding: "8px" }}
                    />
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: "100%",
                        padding: "10px",
                        backgroundColor: loading ? "#ccc" : "#007bff",
                        color: "white",
                        border: "none",
                        cursor: loading ? "not-allowed" : "pointer"
                    }}
                >
                    {loading ? "Giriş yapılıyor..." : "Giriş Yap"}
                </button>

                {/* localStorage temizleme butonu - geliştirme aşamasında */}
                <button
                    type="button"
                    onClick={() => {
                        localStorage.clear();
                        window.location.reload();
                    }}
                    style={{
                        width: "100%",
                        padding: "5px",
                        marginTop: "5px",
                        backgroundColor: "#dc3545",
                        color: "white",
                        border: "none",
                        fontSize: "12px"
                    }}
                >
                    localStorage Temizle ve Yenile
                </button>

                {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
            </form>
        </div>
    );
}

export default Login;
