import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jwtDecode } from "jwt-decode"; // JWT token'ı çözümlemek için gerekli kütüphane

function UserDashboard() {
    const navigate = useNavigate();
    const [projects, setProjects] = useState([]);
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem("token");

        // Token yoksa veya yetkili değilse yönlendir
        if (!token) {
            navigate("/login");
            return;
        }

        try {
            // JWT token'ı çözümleyerek roller ve username bilgisini alıyoruz
            const decodedToken = jwtDecode(token);
            const roles = decodedToken.roles || [];
            const authorized = roles.some((r) => r === "CALISAN");

            if (!authorized) {
                setError("⚠️ Bu sayfaya erişim yetkiniz yoktur.");
                setLoading(false);
                return;
            }

        } catch (err) {
            // Token çözümleme hatası, süresi dolmuş veya geçersiz token
            setError("Geçersiz veya süresi dolmuş token. Lütfen tekrar giriş yapın.");
            localStorage.clear();
            navigate("/login");
            setLoading(false);
            return;
        }

        // Backend'den projeleri çekme işlemi
        axios
            .get("http://localhost:8080/api/project/my_project", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
            .then((res) => {
                setProjects(res.data);
                setLoading(false);
            })
            .catch((err) => {
                // Hata durumunda, hata mesajına göre kullanıcıya bilgi ver
                if (err.response && (err.response.status === 401 || err.response.status === 403)) {
                    setError("Oturum süreniz doldu veya yetkiniz yok. Lütfen tekrar giriş yapın.");
                    localStorage.clear();
                    navigate("/login");
                } else {
                    setError("Projeler alınamadı. Sunucu hatası.");
                }
                setLoading(false);
            });
    }, [navigate]);

    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
    };

    if (loading) {
        return <p>Yükleniyor...</p>;
    }

    if (error) {
        return (
            <div style={{ padding: "20px", color: "red" }}>
                <h2>{error}</h2>
                <button onClick={handleLogout}>Giriş Sayfasına Dön</button>
            </div>
        );
    }

    return (
        <div>
            <h1>👋 Kullanıcı Paneline Hoşgeldiniz</h1>
            <button onClick={handleLogout}>Çıkış Yap</button>

            <h2>Size Atanmış Projeler</h2>
            {projects.length > 0 ? (
                <ul>
                    {projects.map((p) => (
                        <li key={p.project_id}>
                            <strong>{p.name}</strong> - {p.description} ({p.project_status})
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Henüz size atanmış proje bulunmamaktadır.</p>
            )}
        </div>
    );
}

export default UserDashboard;
