import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function UserDashboard() {
    const navigate = useNavigate();
    const [hasRole, setHasRole] = useState(true);
    const [projects, setProjects] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        const roles = JSON.parse(localStorage.getItem("role") || "[]");
        const authorized = roles.some((r) => r.authority === "CALISAN");

        if (!authorized) {
            setHasRole(false);
            return;
        }

        const token = localStorage.getItem("token");
        const username = localStorage.getItem("username"); // username'yi localStorage'dan alƒ±yoruz

        if (!token || !username) {
            setError("Giri≈ü bilgileri eksik.");
            return;
        }

        axios
            .get("http://localhost:8080/api/project/my_project", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                params: { username: username },
            })
            .then((res) => {
                setProjects(res.data);
            })
            .catch(() => {
                setError("Projeler alƒ±namadƒ±.");
            });
    }, []);

    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
    };

    if (!hasRole) {
        return (
            <div style={{ padding: "20px", color: "red" }}>
                <h2>‚ö†Ô∏è Rol yetkiniz yoktur.</h2>
                <button onClick={handleLogout}>Giri≈ü Sayfasƒ±na D√∂n</button>
            </div>
        );
    }

    return (
        <div>
            <h1>üëã Kullanƒ±cƒ± Paneline Ho≈ügeldiniz</h1>
            <button onClick={handleLogout}>√áƒ±kƒ±≈ü Yap</button>

            {error && <p style={{ color: "red" }}>{error}</p>}

            <h2>Kendime Atanmƒ±≈ü Projeler</h2>
            {projects.length > 0 ? (
                <ul>
                    {projects.map((p) => (
                        <li key={p.id}>
                            {p.name} - {p.description} - {p.project_status}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Projeler bulunamadƒ± veya hen√ºz atanmadƒ±nƒ±z.</p>
            )}
        </div>
    );
}

export default UserDashboard;
