        const token = localStorage.getItem("token");  // Token'Ä± al

        axios.get("http://localhost:8080/api/project/list", {
            headers: {
                Authorization: `Bearer ${token}`  // Header'a token ekle
            }
        })
            .then((res) => setProjects(res.data))
            .catch((err) => {
                console.error(err);
