import React, { useEffect, useState } from "react";
import axios from "axios";
import WorkersForm from "./WorkersForm";

function WorkerList() {
    const [workers, setWorkers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [editingWorker, setEditingWorker] = useState(null);

    // Rol kontrolü (PROJE_YONETICISI olan admin)
    const roles = JSON.parse(localStorage.getItem("role") || "[]");
    const isAdmin = roles.some((r) => r.authority === "PROJE_YONETICISI");

    const fetchWorkers = async () => {
        setLoading(true);
        try {
            const res = await axios.get("http://localhost:8080/api/worker/list");
            setWorkers(res.data);
            setError("");
        } catch (err) {
            setError("Çalışanlar yüklenirken hata oluştu.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchWorkers();
    }, []);

    const handleDelete = async (id) => {
        if (!window.confirm("Çalışanı silmek istediğinize emin misiniz?")) return;

        try {
            await axios.delete(`http://localhost:8080/api/worker/delete/${id}`);
            setWorkers((prev) => prev.filter((w) => w.id !== id));
            if (editingWorker && editingWorker.id === id) {
                setEditingWorker(null);
            }
        } catch (err) {
            setError("Çalışan silinirken hata oluştu.");
            console.error(err);
        }
    };

    const handleSubmit = async (formData) => {
        try {
            if (formData.id) {
                await axios.put(`http://localhost:8080/api/worker/update/${formData.id}`, formData);
            } else {
                await axios.post("http://localhost:8080/api/worker/create", formData);
            }
            fetchWorkers();
            setEditingWorker(null);
            setError("");
        } catch (err) {
            setError("Çalışan kaydedilirken hata oluştu.");
            console.error(err);
        }
    };

    const handleCancelEdit = () => {
        setEditingWorker(null);
    };

    return (
        <div>
            <h2>Çalışan Listesi</h2>

            {error && <p style={{ color: "red" }}>{error}</p>}
            {loading && <p>Yükleniyor...</p>}

            {isAdmin && (
                <WorkersForm
                    onSubmit={handleSubmit}
                    initialData={editingWorker}
                    onCancel={handleCancelEdit}
                />
            )}

            <ul>
                {workers.length === 0 && !loading && <li>Çalışan bulunamadı.</li>}
                {workers.map((worker) => (
                    <li key={worker.id} style={{ marginBottom: "10px" }}>
                        {worker.name} - {worker.age} - {worker.gender}
                        {isAdmin && (
                            <>
                                <button
                                    onClick={() => handleDelete(worker.id)}
                                    style={{ marginLeft: "10px" }}
                                >
                                    Sil
                                </button>
                                <button
                                    onClick={() => setEditingWorker(worker)}
                                    style={{ marginLeft: "5px" }}
                                >
                                    Düzenle
                                </button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default WorkerList;
