import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        try {
            // Backend'e login isteği gönder
            const response = await axios.post("http://localhost:8080/api/login", {
                username,
                password,
            });

            // Backend'den kullanıcı bilgisi ve rol geliyor
            // Örnek: { username: "...", roles: [{authority: "PROJE_YONETICISI"}] }
            const userData = response.data;

            if (userData && userData.roles && userData.roles.length > 0) {
                // Rol bilgisini localStorage'a kaydet
                localStorage.setItem("role", JSON.stringify(userData.roles));

                // Rol bazlı yönlendirme (ilk rolü alıyoruz)
                const role = userData.roles[0].authority;

                if (role === "PROJE_YONETICISI") {
                    navigate("/admin-dashboard");
                } else if (role === "CALISAN") {
                    navigate("/user-dashboard");
                } else {
                    setError("Yetkisiz rol.");
                }
            } else {
                setError("Rol bilgisi alınamadı.");
            }
        } catch (err) {
            setError("Giriş başarısız. Kullanıcı adı veya şifre yanlış.");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                placeholder="Kullanıcı Adı"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
            />
            <input
                placeholder="Şifre"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
            <button type="submit">Giriş Yap</button>
            {error && <p style={{ color: "red" }}>{error}</p>}
        </form>
    );
}

export default Login;
