// src/pages/AdminDashboard.jsx (veya src/AdminDashboard.jsx)
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
// ProjectForm ve WorkerForm'u artık doğrudan burada render etmeyeceğiz,
// bu yüzden importlarına gerek kalmadı.
// import ProjectForm from "./projects/ProjectForm";
// import WorkerForm from "./workers/WorkerForm";
import ProjectList from "./projects/ProjectList"; // Bu bileşen kendi formunu yönetecek
import WorkersList from "./workers/WorkersList"; // Bu bileşen kendi formunu yönetecek
import axios from "axios"; // Gerekirse genel API çağrıları için
import { jwtDecode } from "jwt-decode"; // Token çözmek için

function AdminDashboard() {
    const navigate = useNavigate();
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(true);
    const [username, setUsername] = useState(""); // Kullanıcı adını göstermek için

    const token = localStorage.getItem("token");

    useEffect(() => {
        if (!token) {
            setError("Giriş yapmanız gerekiyor.");
            setLoading(false);
            navigate("/login");
            return;
        }

        try {
            const decodedToken = jwtDecode(token);
            const roles = decodedToken.roles || [];
            const user = decodedToken.sub; // Genellikle 'sub' alanı kullanıcı adıdır

            // Sadece 'PROJE_YONETICISI' rolüne sahip kullanıcıların erişimine izin ver
            const authorized = roles.some((r) => r === "PROJE_YONETICISI");

            if (!authorized) {
                setError("⚠️ Bu sayfaya erişim yetkiniz yoktur. 'PROJE_YONETICISI' rolü gereklidir.");
                setLoading(false);
                // navigate("/unauthorized"); // Yetkisiz kullanıcılar için özel bir sayfa olabilir
                return;
            }

            setUsername(user); // Kullanıcı adını state'e kaydet
            setLoading(false); // Yükleme tamamlandı

        } catch (err) {
            console.error("Token doğrulama hatası:", err);
            setError("Geçersiz veya süresi dolmuş token. Lütfen tekrar giriş yapın.");
            localStorage.clear();
            setLoading(false);
            navigate("/login");
        }
    }, [token, navigate]);

    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
    };

    // fetchProjects ve fetchWorkers fonksiyonları artık AdminDashboard'da doğrudan kullanılmayacak
    // çünkü ProjectList ve WorkersList kendi verilerini kendileri çekecekler.
    // Bu yüzden bu fonksiyonları kaldırdım.
    /*
    const fetchProjects = () => {
        const token = localStorage.getItem("token");
        axios
            .get("http://localhost:8080/api/project/list", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((res) => setProjects(res.data))
            .catch((err) => console.error("Proje listesi alınamadı:", err));
    };

    const fetchWorkers = () => {
        const token = localStorage.getItem("token");
        axios
            .get("http://localhost:8080/api/worker/list", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((res) => setWorkers(res.data))
            .catch((err) => console.error("Çalışan listesi alınamadı:", err));
    };
    */

    // handleAddWorker fonksiyonu da artık doğrudan burada kullanılmayacak
    // çünkü WorkerList kendi ekleme/güncelleme mantığını yönetecek.
    /*
    const handleAddWorker = (workerData) => {
        const token = localStorage.getItem("token");
        axios
            .post("http://localhost:8080/api/worker/create", workerData, {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then(() => {
                fetchWorkers(); // Çalışan listesi güncellenir
                alert("✅ Çalışan kaydedildi!");
                setMessage("✅ Çalışan kaydedildi!");
                setTimeout(() => setMessage(""), 3000);
            })
            .catch((err) => console.error(err));
    };
    */

    // Sayfa ilk yüklendiğinde verileri çekme useEffect'i de kaldırıldı,
    // çünkü alt bileşenler kendi verilerini yönetecek.
    /*
    useEffect(() => {
        fetchProjects();
        fetchWorkers();
    }, []);
    */

    if (loading) {
        return (
            <div style={{ padding: "20px", textAlign: "center", fontSize: "1.2em" }}>
                <p>Yönetici Paneli Yükleniyor...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ padding: "20px", color: "red", textAlign: "center" }}>
                <h2>{error}</h2>
                <button
                    onClick={handleLogout}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#007bff",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        marginTop: "15px"
                    }}
                >
                    Giriş Sayfasına Dön
                </button>
            </div>
        );
    }

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "1200px", margin: "0 auto" }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "30px", borderBottom: "1px solid #eee", paddingBottom: "20px" }}>
                <h1 style={{ color: "#333", margin: 0 }}>👋 Yönetici Paneline Hoşgeldiniz, {username}!</h1>
                <button
                    onClick={handleLogout}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#dc3545",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer"
                    }}
                >
                    Çıkış Yap
                </button>
            </div>

            {/* Mesaj state'i de kaldırıldı, çünkü alt bileşenler kendi mesajlarını yönetecek */}
            {/*
            {message && (
                <div
                    style={{
                        marginBottom: "20px",
                        padding: "10px",
                        border: "1px solid green",
                        color: "green",
                        borderRadius: "5px",
                        maxWidth: "400px",
                    }}
                >
                    {message}
                </div>
            )}
            */}

            {/* Proje Ekle ve Proje Listesi bölümleri ProjectList bileşeni tarafından yönetilecek */}
            <section style={{ marginBottom: "40px" }}>
                <h2>📋 Proje Yönetimi</h2>
                <ProjectList />
            </section>

            {/* Çalışan Ekle ve Çalışanlar bölümleri WorkersList bileşeni tarafından yönetilecek */}
            <section>
                <h2>👥 Çalışan Yönetimi</h2>
                <WorkersList />
            </section>
        </div>
    );
}

export default AdminDashboard;
