import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

function Login() {
    const { state } = useLocation(); // role burada geliyor
    const navigate = useNavigate();
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = () => {
        // Gerçek doğrulama backendde yapılacak (şimdilik frontend simülasyonu)
        if (state?.role === "admin") {
            navigate("/admin");
        } else if (state?.role === "user") {
            navigate("/user");
        }
    };
    // Örnek: kullanıcı admin ise
    localStorage.setItem("role", "admin");

    // kullanıcıysa
    localStorage.setItem("role", "user");


    return (
        <div>
            <h2>{state?.role === "admin" ? "Admin Girişi" : "Kullanıcı Girişi"}</h2>
            <input
                type="text"
                placeholder="Kullanıcı Adı"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Şifre"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Giriş Yap</button>
        </div>
    );
}

export default Login;
