// src/pages/workers/WorkerForm.jsx
import React, { useState, useEffect } from "react";

function WorkerForm({ onSubmit, initialData, onCancel, onDelete }) {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [gender, setGender] = useState("");

    useEffect(() => {
        if (initialData) {
            setName(initialData.name || "");
            setAge(initialData.age !== undefined ? initialData.age : "");
            setGender(initialData.gender || "");
        } else {
            clearForm();
        }
    }, [initialData]);

    const clearForm = () => {
        setName("");
        setAge("");
        setGender("");
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const ageNumber = Number(age);
        onSubmit({
            id: initialData ? initialData.id : undefined,
            name: name.trim(),
            age: ageNumber,
            gender,
        });
        if (!initialData) {
            clearForm();
        }
    };

    const handleDelete = () => {
        if (initialData && initialData.id) {
            onDelete(initialData.id);
            clearForm();
        }
    };

    return (
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}> {/* Formu dikey hizalar ve elemanlar arasına boşluk ekler */}
            <div style={{ display: "flex", flexDirection: "column" }}> {/* Her input grubu için div */}
                <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#666" }}>
                    Ad:
                </label>
                <input
                    type="text"
                    placeholder="Ad"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    style={{
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                        fontSize: "1em"
                    }}
                />
            </div>
            <div style={{ display: "flex", flexDirection: "column" }}>
                <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#666" }}>
                    Yaş:
                </label>
                <input
                    type="number"
                    placeholder="Yaş"
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                    required
                    min={18}
                    max={100}
                    style={{
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                        fontSize: "1em"
                    }}
                />
            </div>
            <div style={{ display: "flex", flexDirection: "column" }}>
                <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#666" }}>
                    Cinsiyet:
                </label>
                <select
                    value={gender}
                    onChange={(e) => setGender(e.target.value)}
                    required
                    style={{
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                        fontSize: "1em",
                        backgroundColor: "white" // Select kutusu için arka plan rengi
                    }}
                >
                    <option value="">Cinsiyet Seçin</option>
                    <option value="Erkek">Erkek</option>
                    <option value="Kadın">Kadın</option>
                    <option value="Diğer">Diğer</option>
                </select>
            </div>
            {/* Butonları içeren div'e stil ekledik: Ortalar ve boşluk verir */}
            <div style={{ marginTop: "10px", display: "flex", justifyContent: "center", gap: "10px" }}>
                <button
                    type="submit"
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#007bff", // Mavi renk
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        fontSize: "1em",
                        fontWeight: "bold"
                    }}
                >
                    {initialData ? "Güncelle" : "Ekle"}
                </button>
                {initialData && onCancel && (
                    <button
                        type="button"
                        onClick={onCancel}
                        style={{
                            padding: "10px 20px",
                            backgroundColor: "#6c757d", // Gri renk
                            color: "white",
                            border: "none",
                            borderRadius: "5px",
                            cursor: "pointer",
                            fontSize: "1em",
                            fontWeight: "bold"
                        }}
                    >
                        İptal
                    </button>
                )}
                {initialData && onDelete && (
                    <button
                        type="button"
                        onClick={handleDelete}
                        style={{
                            padding: "10px 20px",
                            backgroundColor: "#dc3545", // Kırmızı renk
                            color: "white",
                            border: "none",
                            borderRadius: "5px",
                            cursor: "pointer",
                            fontSize: "1em",
                            fontWeight: "bold"
                        }}
                    >
                        Sil
                    </button>
                )}
            </div>
        </form>
    );
}

export default WorkerForm;
