import React, { useState, useEffect } from "react";

function ProjectForm({ onSubmit, initialData, onCancel, onDelete }) {
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [projectStatus, setProjectStatus] = useState("");

    useEffect(() => {
        if (initialData) {
            setName(initialData.name || "");
            setDescription(initialData.description || "");
            setProjectStatus(initialData.project_status || "");
        } else {
            clearForm();
        }
    }, [initialData]);

    const clearForm = () => {
        setName("");
        setDescription("");
        setProjectStatus("");
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit({
            id: initialData ? initialData.id : undefined,
            name: name.trim(),
            description: description.trim(),
            project_status: projectStatus.trim(),
        });
        if (!initialData) {
            clearForm();
        }
    };

    const handleDelete = () => {
        if (initialData && initialData.id) {
            onDelete(initialData.id);
            clearForm();
        }
    };

    return (
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            <div style={{ display: "flex", flexDirection: "column" }}>
                <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#523360" }}> {/* color1 */}
                    Proje Adı:
                </label>
                <input
                    type="text"
                    placeholder="Proje Adı"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    style={{
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #ca4c7e", // color3
                        fontSize: "1em"
                    }}
                />
            </div>
            <div style={{ display: "flex", flexDirection: "column" }}>
                <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#523360" }}> {/* color1 */}
                    Açıklama:
                </label>
                <input
                    type="text"
                    placeholder="Açıklama"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                    style={{
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #ca4c7e", // color3
                        fontSize: "1em"
                    }}
                />
            </div>
            <div style={{ display: "flex", flexDirection: "column" }}>
                <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#523360" }}> {/* color1 */}
                    Durum:
                </label>
                <input
                    type="text"
                    placeholder="Proje Durumu"
                    value={projectStatus}
                    onChange={(e) => setProjectStatus(e.target.value)}
                    required
                    style={{
                        padding: "10px",
                        borderRadius: "5px",
                        border: "1px solid #ca4c7e", // color3
                        fontSize: "1em"
                    }}
                />
            </div>
            <div style={{ marginTop: "10px", display: "flex", justifyContent: "center", gap: "10px" }}>
                <button
                    type="submit"
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#86396e", // color2
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        fontSize: "1em",
                        fontWeight: "bold",
                        transition: "background-color 0.3s ease",
                    }}
                    onMouseOver={(e) => e.target.style.backgroundColor = "#523360"}
                    onMouseOut={(e) => e.target.style.backgroundColor = "#86396e"}
                >
                    {initialData ? "Güncelle" : "Ekle"}
                </button>
                {initialData && onCancel && (
                    <button
                        type="button"
                        onClick={onCancel}
                        style={{
                            padding: "10px 20px",
                            backgroundColor: "#ff788e", // color4
                            color: "white",
                            border: "none",
                            borderRadius: "5px",
                            cursor: "pointer",
                            fontSize: "1em",
                            fontWeight: "bold",
                            transition: "background-color 0.3s ease",
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = "#ca4c7e"}
                        onMouseOut={(e) => e.target.style.backgroundColor = "#ff788e"}
                    >
                        İptal
                    </button>
                )}
                {initialData && onDelete && (
                    <button
                        type="button"
                        onClick={handleDelete}
                        style={{
                            padding: "10px 20px",
                            backgroundColor: "#ffb69a", // color5
                            color: "#523360", // color1
                            border: "none",
                            borderRadius: "5px",
                            cursor: "pointer",
                            fontSize: "1em",
                            fontWeight: "bold",
                            transition: "background-color 0.3s ease",
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = "#ff788e"}
                        onMouseOut={(e) => e.target.style.backgroundColor = "#ffb69a"}
                    >
                        Sil
                    </button>
                )}
            </div>
        </form>
    );
}

export default ProjectForm;
