// src/pages/projects/ProjectForm.jsx
import React, { useState, useEffect } from "react";
import axios from "axios"; // axios'a burada ihtiyacınız olmayabilir, sadece onSubmit prop'u yeterli

function ProjectForm({ onSubmit, initialData, onCancel, onDelete }) {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projectStatus, setProjectStatus] = useState("");

  useEffect(() => {
    if (initialData) {
      setName(initialData.name || "");
      setDescription(initialData.description || "");
      setProjectStatus(initialData.project_status || "");
    } else {
      clearForm();
    }
  }, [initialData]);

  const clearForm = () => {
    setName("");
    setDescription("");
    setProjectStatus("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      id: initialData ? initialData.id : undefined,
      name: name.trim(),
      description: description.trim(),
      project_status: projectStatus.trim(),
    });
    if (!initialData) {
      clearForm();
    }
  };

  const handleDelete = () => {
    if (initialData && initialData.id) {
      onDelete(initialData.id);
      clearForm();
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}> {/* Formu dikey hizala ve boşluk ekle */}
      <div style={{ display: "flex", flexDirection: "column" }}> {/* Her input grubu için div */}
        <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#666" }}>
          Proje Adı:
        </label>
        <input
          type="text"
          placeholder="Proje Adı"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={{
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ddd",
            fontSize: "1em"
          }}
        />
      </div>
      <div style={{ display: "flex", flexDirection: "column" }}>
        <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#666" }}>
          Açıklama:
        </label>
        <input
          type="text"
          placeholder="Açıklama"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          style={{
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ddd",
            fontSize: "1em"
          }}
        />
      </div>
      <div style={{ display: "flex", flexDirection: "column" }}>
        <label style={{ marginBottom: "5px", fontWeight: "bold", color: "#666" }}>
          Durum:
        </label>
        <input
          type="text"
          placeholder="Proje Durumu"
          value={projectStatus}
          onChange={(e) => setProjectStatus(e.target.value)}
          required
          style={{
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ddd",
            fontSize: "1em"
          }}
        />
      </div>
      <div style={{ marginTop: "10px", display: "flex", justifyContent: "center", gap: "10px" }}> {/* Butonları ortala */}
        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            fontSize: "1em",
            fontWeight: "bold"
          }}
        >
          {initialData ? "Güncelle" : "Ekle"}
        </button>
        {initialData && onCancel && (
          <button
            type="button"
            onClick={onCancel}
            style={{
              padding: "10px 20px",
              backgroundColor: "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontSize: "1em",
              fontWeight: "bold"
            }}
          >
            İptal
          </button>
        )}
        {initialData && onDelete && (
          <button
            type="button"
            onClick={handleDelete}
            style={{
              padding: "10px 20px",
              backgroundColor: "#dc3545",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontSize: "1em",
              fontWeight: "bold"
            }}
          >
            Sil
          </button>
        )}
      </div>
    </form>
  );
}

export default ProjectForm;
