// src/pages/projects/ProjectForm.jsx
import React, { useState, useEffect } from "react";

function ProjectForm({ onSubmit, initialData, onCancel, onDelete }) {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projectStatus, setProjectStatus] = useState("");

  useEffect(() => {
    if (initialData) {
      setName(initialData.name || "");
      setDescription(initialData.description || "");
      setProjectStatus(initialData.project_status || "");
    } else {
      clearForm();
    }
  }, [initialData]);

  const clearForm = () => {
    setName("");
    setDescription("");
    setProjectStatus("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      id: initialData ? initialData.id : undefined, // Backend'de 'id' olarak kayıtlı olduğunu varsayıyoruz
      name: name.trim(),
      description: description.trim(),
      project_status: projectStatus.trim(),
    });
    if (!initialData) {
      clearForm();
    }
  };

  const handleDelete = () => {
    if (initialData && initialData.id) {
      onDelete(initialData.id);
      clearForm();
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Proje Adı:
          <input
            type="text"
            placeholder="Proje Adı"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Açıklama:
          <input
            type="text"
            placeholder="Açıklama"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </label>
      </div>
      <div>
        <label>
          Durum:
          <input
            type="text"
            placeholder="Proje Durumu"
            value={projectStatus}
            onChange={(e) => setProjectStatus(e.target.value)}
            required
          />
        </label>
      </div>
      <div style={{ marginTop: "10px" }}>
        <button type="submit">{initialData ? "Güncelle" : "Ekle"}</button>
        {initialData && onCancel && (
          <button
            type="button"
            onClick={onCancel}
            style={{ marginLeft: "10px" }}
          >
            İptal
          </button>
        )}
        {initialData && onDelete && (
          <button
            type="button"
            onClick={handleDelete}
            style={{ marginLeft: "10px", backgroundColor: "red", color: "white" }}
          >
            Sil
          </button>
        )}
      </div>
    </form>
  );
}

export default ProjectForm;
