// pages/projects/ProjectList.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import ProjectForm from "../../components/ProjectForm";

function ProjectList() {
    const [projects, setProjects] = useState([]);
    const [editingProject, setEditingProject] = useState(null);
    const isAdmin = localStorage.getItem("role") === "admin";

    useEffect(() => {
        axios.get("http://localhost:8080/api/project/list")
            .then(res => setProjects(res.data))
            .catch(err => console.error("Veri alınırken hata:", err));
    }, []);

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:8080/api/project/delete/${id}`);
        setProjects(prev => prev.filter(p => p.id !== id));
    };

    const handleSubmit = async (formData) => {
        if (editingProject) {
            await axios.put(`http://localhost:8080/api/project/update/${editingProject.id}`, formData);
        } else {
            await axios.post("http://localhost:8080/api/project/create", formData);
        }
        const res = await axios.get("http://localhost:8080/api/project/list");
        setProjects(res.data);
        setEditingProject(null);
    };

    return (
        <div>
            <h2>Proje Listesi</h2>
            <ul>
                {projects.map(pro => (
                    <li key={pro.id}>
                        {pro.name} - {pro.description} - {pro.project_status}
                        {isAdmin && (
                            <>
                                <button onClick={() => handleDelete(pro.id)}>Sil</button>
                                <button onClick={() => setEditingProject(pro)}>Güncelle</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>

            {isAdmin && (
                <ProjectForm onSubmit={handleSubmit} initialData={editingProject} />
            )}
        </div>
    );
}

export default ProjectList;
