import axios from "axios";
import React, { useEffect, useState } from "react";
import ProjectForm from "./ProjectForm";

function ProjectList() {
    const [projects, setProjects] = useState([]);
    const [editingProject, setEditingProject] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // localStorage’dan rol bilgisi alınıyor ve admin kontrolü yapılıyor
    const roles = JSON.parse(localStorage.getItem("role") || "[]");
    const isAdmin = roles.some((r) => r.authority === "PROJE_YONETICISI");

    // Projeleri backend’den çek
    const fetchProjects = async () => {
        setLoading(true);
        try {
            const res = await axios.get("http://localhost:8080/api/project/list");
            setProjects(res.data);
            setError("");
        } catch (err) {
            setError("Projeler yüklenirken hata oluştu.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    // Silme işlemi
    const handleDelete = async (id) => {
        if (!window.confirm("Projeyi silmek istediğinize emin misiniz?")) return;

        try {
            await axios.delete(`http://localhost:8080/api/project/delete/${id}`);
            // Silinen projeyi state’den çıkar
            setProjects((prev) => prev.filter((p) => p.id !== id));
            // Eğer silinen proje düzenleniyorsa formu kapat
            if (editingProject && editingProject.id === id) {
                setEditingProject(null);
            }
        } catch (error) {
            setError("Projeyi silerken hata oluştu.");
            console.error(error);
        }
    };

    // Yeni ekleme veya güncelleme işlemi
    const handleSubmit = async (formData) => {
        try {
            if (formData.id) {
                // Güncelleme isteği
                await axios.put(`http://localhost:8080/api/project/update/${formData.id}`, formData);
            } else {
                // Yeni proje ekleme
                await axios.post("http://localhost:8080/api/project/create", formData);
            }
            // Listeyi yenile
            fetchProjects();
            setEditingProject(null);
            setError("");
        } catch (error) {
            setError("Projeyi kaydederken hata oluştu.");
            console.error(error);
        }
    };

    // Form iptal edildiğinde düzenlemeyi kapat
    const handleCancelEdit = () => {
        setEditingProject(null);
    };

    return (
        <div>
            <h2>Proje Listesi</h2>

            {/* Hata mesajı */}
            {error && <p style={{ color: "red" }}>{error}</p>}

            {/* Yükleniyor mesajı */}
            {loading && <p>Yükleniyor...</p>}

            {/* Sadece admin formu görebilir */}
            {isAdmin && (
                <ProjectForm
                    onSubmit={handleSubmit}
                    initialData={editingProject}
                    onCancel={handleCancelEdit}
                />
            )}

            <ul>
                {projects.length === 0 && !loading && <li>Proje bulunamadı.</li>}
                {projects.map((pro) => (
                    <li key={pro.id} style={{ marginBottom: "10px" }}>
                        <strong>{pro.name}</strong> - {pro.description} - {pro.project_status}
                        {isAdmin && (
                            <>
                                <button
                                    onClick={() => handleDelete(pro.id)}
                                    style={{ marginLeft: "10px" }}
                                >
                                    Sil
                                </button>
                                <button
                                    onClick={() => setEditingProject(pro)}
                                    style={{ marginLeft: "5px" }}
                                >
                                    Düzenle
                                </button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ProjectList;
