import axios from "axios";
import React, { useEffect, useState } from "react";

function ProjectList() {
    const [project, setProject] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/api/project/list")
            .then(res => {
                setProject(res.data);
                console.log("Veri geldi:", res.data);
            })
            .catch(err => {
                console.error("Veri alınırken hata:", err);
            });
    }, []);
    {
        isAdmin && (
            <ProjectForm
                onSubmit={handleSubmit}
                initialData={editingProject}
            />
        )
    }


    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/api/project/delete/${id}`);
            setProject(prev => prev.filter(p => p.id !== id)); // listedeki sil
        } catch (error) {
            console.error("Silme hatası:", error);
        }
    };


    return (
        <div>
            <h2>Proje Listesi</h2>
            <ul>
                {project.map(pro => (
                    <li key={pro.id}>
                        {pro.name} - {pro.description} - {pro.project_status}
                        <button onClick={() => handleDelete(pro.id)}>Sil</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ProjectList;
