import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import ProjectList from "./pages/projects/ProjectList";
import WorkersList from "./pages/workers/WorkersList";
import Login from "./pages/Login";
import AdminDashboard from "./pages/AdminDashboard";
import UserDashboard from "./pages/UserDashboard";

// Rol bazlı özel route bileşeni
function PrivateRoute({ children, role }) {
  // localStorage'dan rol bilgisi array olarak alınıyor
  const roles = JSON.parse(localStorage.getItem("role") || "[]");

  // Eğer rol yoksa veya istenen rol yoksa login sayfasına yönlendir
  if (!roles.some((r) => r.authority === role)) {
    return <Navigate to="/login" replace />;
  }

  // Yetki varsa istediği sayfayı render et
  return children;
}

function App() {
  return (
    <Router>
      <Routes>
        {/* Login Sayfası */}
        <Route path="/login" element={<Login />} />

        {/* Admin Panel ve ona özel sayfalar */}
        <Route
          path="/admin-dashboard"
          element={
            <PrivateRoute role="PROJE_YONETICISI">
              <AdminDashboard />
            </PrivateRoute>
          }
        />
        <Route
          path="/projects"
          element={
            <PrivateRoute role="PROJE_YONETICISI">
              <ProjectList />
            </PrivateRoute>
          }
        />
        <Route
          path="/workers"
          element={
            <PrivateRoute role="PROJE_YONETICISI">
              <WorkersList />
            </PrivateRoute>
          }
        />

        {/* Kullanıcı paneli */}
        <Route
          path="/user-dashboard"
          element={
            <PrivateRoute role="CALISAN">
              <UserDashboard />
            </PrivateRoute>
          }
        />

        {/* Anasayfa veya root erişimi otomatik yönlendirme için */}
        <Route
          path="/"
          element={
            // Rol kontrolü ve varsa yönlendirme, yoksa login sayfasına
            (() => {
              const roles = JSON.parse(localStorage.getItem("role") || "[]");
              if (roles.some((r) => r.authority === "PROJE_YONETICISI")) {
                return <Navigate to="/admin-dashboard" replace />;
              }
              if (roles.some((r) => r.authority === "CALISAN")) {
                return <Navigate to="/user-dashboard" replace />;
              }
              return <Navigate to="/login" replace />;
            })()
          }
        />

        {/* Diğer tüm bilinmeyen yollar login'e yönlendir */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
