// src/pages/workers/WorkerForm.jsx
import React, { useState, useEffect } from "react";

function WorkerForm({ onSubmit, initialData, onCancel, onDelete }) {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [gender, setGender] = useState("");

    useEffect(() => {
        if (initialData) {
            setName(initialData.name || "");
            setAge(initialData.age !== undefined ? initialData.age : "");
            setGender(initialData.gender || "");
        } else {
            clearForm();
        }
    }, [initialData]);

    const clearForm = () => {
        setName("");
        setAge("");
        setGender("");
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const ageNumber = Number(age);
        onSubmit({
            id: initialData ? initialData.worker_id : undefined, // Backend'e uyumlu 'worker_id' kullanın
            name: name.trim(),
            age: ageNumber,
            gender,
        });
        if (!initialData) {
            clearForm();
        }
    };

    const handleDelete = () => {
        if (initialData && initialData.worker_id) {
            onDelete(initialData.worker_id);
            clearForm();
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>
                    Ad:
                    <input
                        type="text"
                        placeholder="Ad"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </label>
            </div>
            <div>
                <label>
                    Yaş:
                    <input
                        type="number"
                        placeholder="Yaş"
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                        required
                        min={18}
                        max={100}
                    />
                </label>
            </div>
            <div>
                <label>
                    Cinsiyet:
                    <select
                        value={gender}
                        onChange={(e) => setGender(e.target.value)}
                        required
                    >
                        <option value="">Cinsiyet Seçin</option>
                        <option value="Erkek">Erkek</option>
                        <option value="Kadın">Kadın</option>
                        <option value="Diğer">Diğer</option>
                    </select>
                </label>
            </div>
            <div style={{ marginTop: "10px" }}>
                <button type="submit">{initialData ? "Güncelle" : "Ekle"}</button>
                {initialData && onCancel && (
                    <button
                        type="button"
                        onClick={onCancel}
                        style={{ marginLeft: "10px" }}
                    >
                        İptal
                    </button>
                )}
                {initialData && onDelete && (
                    <button
                        type="button"
                        onClick={handleDelete}
                        style={{ marginLeft: "10px", backgroundColor: "red", color: "white" }}
                    >
                        Sil
                    </button>
                )}
            </div>
        </form>
    );
}

export default WorkerForm;
