import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Backend URL konfigürasyonu - Tomcat için
const API_BASE_URL = "http://localhost:8080"; // your-app-name'i gerçek uygulama isminizle değiştirin

// Axios default config
axios.defaults.baseURL = API_BASE_URL;
axios.defaults.timeout = 10000; // 10 saniye timeout

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    // localStorage'dan güvenli veri okuma fonksiyonu
    const getRolesFromStorage = () => {
        try {
            const roleData = localStorage.getItem("role");
            console.log("Raw role data:", roleData, "Type:", typeof roleData);

            if (!roleData || roleData === "undefined" || roleData === "null") {
                return [];
            }

            // Eğer zaten bir array ise direkt döndür
            if (Array.isArray(roleData)) {
                return roleData;
            }

            // String ise parse et
            const parsed = JSON.parse(roleData);
            return Array.isArray(parsed) ? parsed : [];

        } catch (error) {
            console.error("localStorage role verisi bozuk:", error);
            console.log("Bozuk veri temizleniyor...");
            localStorage.removeItem("role");
            return [];
        }
    };

    // Component mount olduğunda zaten giriş yapılmış mı kontrol et
    useEffect(() => {
        console.log("Login component mounted, checking existing roles...");

        try {
            const roles = getRolesFromStorage();
            console.log("Found roles:", roles);

            if (roles && roles.length > 0 && roles[0] && roles[0].authority) {
                const role = roles[0].authority;
                console.log("Redirecting based on role:", role);

                if (role === "PROJE_YONETICISI") {
                    navigate("/admin-dashboard", { replace: true });
                } else if (role === "CALISAN") {
                    navigate("/user-dashboard", { replace: true });
                }
            }
        } catch (error) {
            console.error("useEffect error:", error);
            localStorage.clear();
        }
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setLoading(true);

        try {
            console.log("Attempting login to:", `${API_BASE_URL}/api/auth/login`);

            // GEÇICI: Backend yoksa mock data ile test
            if (username === "admin" && password === "123") {
                const mockResponse = {
                    data: {
                        roles: [{ authority: "PROJE_YONETICISI" }]
                    }
                };
                console.log("Mock login successful");
                localStorage.setItem("role", JSON.stringify(mockResponse.data.roles));
                localStorage.setItem("username", username);
                navigate("/admin-dashboard", { replace: true });
                return;
            }

            // Backend'e login isteği gönder - düzeltilmiş URL
            const response = await axios.post("/api/auth/login", {
                username,
                password,
            }, {
                // CORS için gerekli olabilir
                withCredentials: false
            });

            console.log("Login response:", response.data);

            // Backend'den kullanıcı bilgisi ve rol geliyor
            const userData = response.data;

            if (userData && userData.role !== undefined && userData.role.length > 0) {

                // Başarılı login sonrası token'ı da kaydet
                localStorage.setItem("role", JSON.stringify(userData.role));
                localStorage.setItem("username", username);

                // Eğer backend JWT token döndürüyorsa kaydet
                if (userData.token) {
                    localStorage.setItem("token", userData.token);
                }

                // Rol bazlı yönlendirme (ilk rolü alıyoruz)
                const role = userData.role;
                console.log("Ramazan:", role.filter((f) => f.authority === "PROJE_YONETICISI"));
                const yonetici = role.filter((f) => f.authority === "PROJE_YONETICISI").length > 0 ? true : false;

                if (yonetici) {
                    navigate("/admin-dashboard", { replace: true });
                } else {
                    navigate("/user-dashboard", { replace: true });
                }
            } else {
                setError("Kullanıcı rol bilgisi alınamadı.");
            }
        } catch (err) {
            console.error("Login error:", err);

            // Daha detaylı hata mesajları
            if (err.code === 'ECONNABORTED') {
                setError("İstek zaman aşımına uğradı. Sunucu yanıt vermiyor.");
            } else if (err.response) {
                // Sunucu yanıt verdi ama hata kodu ile
                const status = err.response.status;
                const message = err.response.data?.message || err.response.statusText;

                if (status === 401) {
                    setError("Geçersiz kullanıcı adı veya şifre.");
                } else if (status === 403) {
                    setError("Bu hesapla giriş yapma yetkiniz yok.");
                } else if (status === 500) {
                    setError("Sunucu hatası. Lütfen daha sonra tekrar deneyin.");
                } else {
                    setError(`Giriş başarısız (${status}): ${message}`);
                }
            } else if (err.request) {
                // İstek gönderildi ama yanıt alınamadı
                setError(`Sunucuya bağlanılamıyor (${API_BASE_URL}). Lütfen:\n- Backend servisinin çalıştığını kontrol edin\n- URL'nin doğru olduğunu kontrol edin\n- Ağ bağlantınızı kontrol edin`);
            } else {
                // İstek hazırlanırken hata oluştu
                setError("Bir hata oluştu. Lütfen tekrar deneyin.");
            }
        } finally {
            setLoading(false);
        }
    };



    // Backend bağlantısını test etme fonksiyonu


    return (
        <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
            <h2>Giriş Yap</h2>

            {/* Backend URL bilgisi ve test butonları */}
            <div style={{ fontSize: "12px", color: "#666", marginBottom: "10px", border: "1px solid #ddd", padding: "8px", borderRadius: "4px" }}>
                <div><strong>Backend URL:</strong> {API_BASE_URL}</div>
                <div><strong>localStorage role:</strong> {localStorage.getItem("role") || "yok"}</div>
                <div><strong>localStorage token:</strong> {localStorage.getItem("token") ? "var" : "yok"}</div>
                <button
                    onClick={testConnection}
                    style={{ marginTop: "5px", fontSize: "11px", padding: "2px 6px", marginRight: "5px" }}
                >
                    Bağlantıyı Test Et
                </button>
                <button
                    onClick={checkTokenExpiry}
                    style={{ marginTop: "5px", fontSize: "11px", padding: "2px 6px" }}
                >
                    Token Kontrolü
                </button>
            </div>

            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: "10px" }}>
                    <input
                        placeholder="Kullanıcı Adı"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        disabled={loading}
                        style={{ width: "100%", padding: "8px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <input
                        placeholder="Şifre"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        disabled={loading}
                        style={{ width: "100%", padding: "8px" }}
                    />
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: "100%",
                        padding: "10px",
                        backgroundColor: loading ? "#ccc" : "#007bff",
                        color: "white",
                        border: "none",
                        cursor: loading ? "not-allowed" : "pointer"
                    }}
                >
                    {loading ? "Giriş yapılıyor..." : "Giriş Yap"}
                </button>

                {/* localStorage temizleme butonu - geliştirme aşamasında */}
                <button
                    type="button"
                    onClick={() => {
                        localStorage.clear();
                        window.location.reload();
                    }}
                    style={{
                        width: "100%",
                        padding: "5px",
                        marginTop: "5px",
                        backgroundColor: "#dc3545",
                        color: "white",
                        border: "none",
                        fontSize: "12px"
                    }}
                >
                    localStorage Temizle ve Yenile
                </button>

                {error && (
                    <div style={{
                        color: error.includes("✅") ? "green" : "red",
                        marginTop: "10px",
                        whiteSpace: "pre-line",
                        fontSize: "14px",
                        border: "1px solid",
                        borderColor: error.includes("✅") ? "green" : "red",
                        padding: "8px",
                        borderRadius: "4px",
                        backgroundColor: error.includes("✅") ? "#f0fff0" : "#fff0f0"
                    }}>
                        {error}
                    </div>
                )}
            </form>
        </div>
    );
}

export default Login;
