import React, { useState } from "react";
import axios from "axios";

function ProjectForm() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projectStatus, setProjectStatus] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token"); // token'ı localStorage'dan al

    axios.post("http://localhost:8080/api/project/create", {
      name,
      description,
      project_status: projectStatus,
    }, {
      headers: {
        Authorization: `Bearer ${token}`  // token'ı header'a ekle
      }
    }).then(() => {
      alert("✅ Proje kaydedildi!");
      setName("");
      setDescription("");
      setProjectStatus("");
    });
  };

  return (
    <div>

      <form onSubmit={handleSubmit}>
        <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Proje Adı" />
        <input value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Açıklama" />
        <input
          value={projectStatus}
          onChange={(e) => setProjectStatus(e.target.value)}
          placeholder="Proje Durumu"
        />
        <button type="submit">Kaydet</button>
      </form>
    </div>
  );
}

export default ProjectForm;
