import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ProjectList from "./projects/ProjectList";
import ProjectForm from "./projects/ProjectForm";
import WorkerList from "./workers/WorkersList";
import WorkerForm from "./workers/WorkerForm"; // âœ… burada dÃ¼zeltildi
import axios from "axios";


function AdminDashboard() {
    const navigate = useNavigate();
    const [projects, setProjects] = useState([]);
    const [workers, setWorkers] = useState([]);
    const [message, setMessage] = useState("");

    // Rol kontrolÃ¼
    useEffect(() => {
        const roles = JSON.parse(localStorage.getItem("role") || "[]");
        const hasRole = roles.some((r) => r.authority === "PROJE_YONETICISI");
        if (!hasRole) {
            localStorage.clear();
            navigate("/login");
        }
    }, [navigate]);

    // Projeleri API'den al
    const fetchProjects = () => {
        const token = localStorage.getItem("token");

        axios
            .get("http://localhost:8080/api/project/list", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
            .then((res) => {
                setProjects(res.data);
            })
            .catch((err) => {
                console.error("Proje listesi alÄ±namadÄ±:", err);
            });
    };


    const fetchWorkers = () => {
        const token = localStorage.getItem("token");

        axios.get("http://localhost:8080/api/worker/list", {
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
            .then((res) => {
                setWorkers(res.data);
            })
            .catch((err) => {
                console.error("Ã‡alÄ±ÅŸan listesi alÄ±namadÄ±:", err);
            });
    };


    const handleAddWorker = (workerData) => {
        const token = localStorage.getItem("token");
        axios.post("http://localhost:8080/api/worker/create", workerData, {
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
            .then(() => {
                fetchWorkers();
                setMessage("âœ… Ã‡alÄ±ÅŸan baÅŸarÄ±yla eklendi!");
                setTimeout(() => setMessage(""), 3000);
            })
            .catch((err) => console.error(err));
    };


    // Ä°lk yÃ¼klendiÄŸinde projeleri Ã§ek
    useEffect(() => {
        fetchProjects();
        fetchWorkers();
    }, []);

    // Ã‡Ä±kÄ±ÅŸ
    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
    };

    return (
        <div>
            <h1>ðŸ‘‹ Admin Paneline HoÅŸgeldiniz</h1>

            <button onClick={handleLogout}>Ã‡Ä±kÄ±ÅŸ Yap</button>

            <hr />

            <h2>âž• Proje Ekle</h2>
            <ProjectForm onProjectCreated={fetchProjects} />

            <hr />

            <h2>ðŸ“‹ Proje Listesi</h2>
            <ProjectList projects={projects} />

            <hr />

            <h2>âž• Ã‡alÄ±ÅŸan Ekle</h2>
            <WorkerForm onSubmit={handleAddWorker} />


            <h2>ðŸ‘¥ Ã‡alÄ±ÅŸanlar</h2>
            <WorkerList />


        </div>
    );
}

export default AdminDashboard;
