import axios from "axios";
import React, { useEffect, useState } from "react";
import ProjectForm from './project/ProjectForm'; // doğru konumda olduğuna emin ol

function ProjectList() {
    const [project, setProject] = useState([]);
    const [editingProject, setEditingProject] = useState(null);
    const isAdmin = localStorage.getItem("role") === "admin"; // string olarak karşılaştır

    useEffect(() => {
        axios.get("http://localhost:8080/api/project/list")
            .then(res => {
                setProject(res.data);
                console.log("Veri geldi:", res.data);
            })
            .catch(err => {
                console.error("Veri alınırken hata:", err);
            });
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/api/project/delete/${id}`);
            setProject(prev => prev.filter(p => p.id !== id));
        } catch (error) {
            console.error("Silme hatası:", error);
        }
    };

    const handleSubmit = async (formData) => {
        try {
            if (formData.id) {
                // Güncelleme
                await axios.put(`http://localhost:8080/api/project/update/${formData.id}`, formData);
            } else {
                // Yeni proje ekleme
                await axios.post("http://localhost:8080/api/project/save", formData);
            }

            // Listeyi güncelle
            const res = await axios.get("http://localhost:8080/api/project/list");
            setProject(res.data);
            setEditingProject(null);
        } catch (error) {
            console.error("Kaydetme hatası:", error);
        }
    };

    return (
        <div>
            <h2>Proje Listesi</h2>

            {isAdmin && (
                <ProjectForm
                    onSubmit={handleSubmit}
                    initialData={editingProject}
                />
            )}

            <ul>
                {project.map(pro => (
                    <li key={pro.id}>
                        {pro.name} - {pro.description} - {pro.project_status}
                        {isAdmin && (
                            <>
                                <button onClick={() => handleDelete(pro.id)}>Sil</button>
                                <button onClick={() => setEditingProject(pro)}>Düzenle</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ProjectList;
