import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ProjectList from "./projects/ProjectList";
import ProjectForm from "./projects/ProjectForm";
import WorkerList from "./workers/WorkersList";
import WorkerForm from "./workers/WorkerForm";
import axios from "axios";

function AdminDashboard() {
    const navigate = useNavigate();
    const [projects, setProjects] = useState([]);
    const [workers, setWorkers] = useState([]);
    const [message, setMessage] = useState("");

    // Rol kontrolü - sadece PROJE_YONETICISI rolü olan giriş yapabilir
    useEffect(() => {
        const roles = JSON.parse(localStorage.getItem("role") || "[]");
        const hasRole = roles.some((r) => r.authority === "PROJE_YONETICISI");
        if (!hasRole) {
            localStorage.clear();
            navigate("/login");
        }
    }, [navigate]);

    // Projeleri API'den çek
    const fetchProjects = () => {
        const token = localStorage.getItem("token");
        axios
            .get("http://localhost:8080/api/project/list", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((res) => setProjects(res.data))
            .catch((err) => console.error("Proje listesi alınamadı:", err));
    };

    // Çalışanları API'den çek
    const fetchWorkers = () => {
        const token = localStorage.getItem("token");
        axios
            .get("http://localhost:8080/api/worker/list", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((res) => setWorkers(res.data))
            .catch((err) => console.error("Çalışan listesi alınamadı:", err));
    };



    // Yeni çalışan ekleme fonksiyonu
    const handleAddWorker = (workerData) => {
        const token = localStorage.getItem("token");
        axios
            .post("http://localhost:8080/api/worker/create", workerData, {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then(() => {
                fetchWorkers(); // Çalışan listesi güncellenir
                alert("✅ Çalışan kaydedildi!");
                setMessage("✅ Çalışan kaydedildi!");
                setTimeout(() => setMessage(""), 3000);
            })
            .catch((err) => console.error(err));
    };

    // Sayfa ilk yüklendiğinde verileri çek
    useEffect(() => {
        fetchProjects();
        fetchWorkers();
    }, []);

    // Çıkış yapma fonksiyonu
    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>👋 Admin Paneline Hoşgeldiniz</h1>

            <button onClick={handleLogout} style={{ marginBottom: "20px" }}>
                Çıkış Yap
            </button>

            {message && (
                <div
                    style={{
                        marginBottom: "20px",
                        padding: "10px",
                        border: "1px solid green",
                        color: "green",
                        borderRadius: "5px",
                        maxWidth: "400px",
                    }}
                >
                    {message}
                </div>
            )}

            <section style={{ marginBottom: "40px" }}>
                <h2>➕ Proje Ekle</h2>
                <ProjectForm onProjectCreated={fetchProjects} />
            </section>

            <section style={{ marginBottom: "40px" }}>
                <h2>📋 Proje Listesi</h2>
                <ProjectList projects={projects} />
            </section>

            <section style={{ marginBottom: "40px" }}>
                <h2>➕ Çalışan Ekle</h2>
                <WorkerForm onSubmit={handleAddWorker} />
            </section>

            <section>
                <h2>👥 Çalışanlar</h2>
                <WorkerList workers={workers} />
            </section>
        </div>
    );
}

export default AdminDashboard;
