import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    // Component mount olduğunda zaten giriş yapılmış mı kontrol et
    useEffect(() => {
        const roles = JSON.parse(localStorage.getItem("role") || "[]");
        if (roles.length > 0) {
            // Zaten giriş yapılmışsa uygun sayfaya yönlendir
            const role = roles[0].authority;
            if (role === "PROJE_YONETICISI") {
                navigate("/admin-dashboard", { replace: true });
            } else if (role === "CALISAN") {
                navigate("/user-dashboard", { replace: true });
            }
        }
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setLoading(true);

        try {
            // Backend'e login isteği gönder
            const response = await axios.post("http://localhost:8080/api/login", {
                username,
                password,
            });

            // Backend'den kullanıcı bilgisi ve rol geliyor
            const userData = response.data;

            if (userData && userData.roles && userData.roles.length > 0) {
                // Rol bilgisini localStorage'a kaydet
                localStorage.setItem("role", JSON.stringify(userData.roles));
                localStorage.setItem("username", username); // Kullanıcı adını da kaydedelim

                // Rol bazlı yönlendirme (ilk rolü alıyoruz)
                const role = userData.roles[0].authority;

                if (role === "PROJE_YONETICISI") {
                    navigate("/admin-dashboard", { replace: true });
                } else if (role === "CALISAN") {
                    navigate("/user-dashboard", { replace: true });
                } else {
                    setError("Geçersiz rol tipi.");
                }
            } else {
                setError("Kullanıcı rol bilgisi alınamadı.");
            }
        } catch (err) {
            console.error("Login error:", err);
            if (err.response) {
                setError(`Giriş başarısız: ${err.response.data.message || 'Sunucu hatası'}`);
            } else if (err.request) {
                setError("Sunucuya bağlanılamıyor. Lütfen bağlantınızı kontrol edin.");
            } else {
                setError("Bir hata oluştu. Lütfen tekrar deneyin.");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
            <h2>Giriş Yap</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: "10px" }}>
                    <input
                        placeholder="Kullanıcı Adı"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        disabled={loading}
                        style={{ width: "100%", padding: "8px" }}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <input
                        placeholder="Şifre"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        disabled={loading}
                        style={{ width: "100%", padding: "8px" }}
                    />
                </div>
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: "100%",
                        padding: "10px",
                        backgroundColor: loading ? "#ccc" : "#007bff",
                        color: "white",
                        border: "none",
                        cursor: loading ? "not-allowed" : "pointer"
                    }}
                >
                    {loading ? "Giriş yapılıyor..." : "Giriş Yap"}
                </button>
                {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
            </form>
        </div>
    );
}

export default Login;
