// src/pages/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

// Yönetim bileşenlerini import edin
import ProjectList from "./projects/ProjectList";
import WorkersList from "./workers/WorkersList";
import ProjectAssignmentForm from "./projects/ProjectAssigmentForm";

function AdminDashboard() {
    const navigate = useNavigate();
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(true);
    const [username, setUsername] = useState("");
    const [worker, setWorker] = useState(null); // Yeni eklenen state
    const [activeAdminSection, setActiveAdminSection] = useState("projects");

    const token = localStorage.getItem("token");

    useEffect(() => {
        if (!token) {
            setError("Giriş yapmanız gerekiyor.");
            setLoading(false);
            navigate("/login");
            return;
        }

        try {
            const decodedToken = jwtDecode(token);
            const roles = decodedToken.roles || [];
            const user = decodedToken.sub; // Kullanıcı adı

            const authorized = roles.some((r) => r === "PROJE_YONETICISI");
            if (!authorized) {
                setError("⚠️ Bu sayfaya erişim yetkiniz yoktur. 'PROJE_YONETICISI' rolü gereklidir.");
                setLoading(false);
                return;
            }

            setUsername(user);

            // --------------------------------------------------
            // 2. ADIM: username ile worker verisini çekme
            // --------------------------------------------------
            // API endpoint'inizi buraya girin.
            // Örneğin, http://localhost:8080/api/workers/by-username/atakan.alkan gibi
            axios.get(`/api/worker/by-username/${user}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
                .then(response => {
                    setWorker(response.data); // worker state'ini doldur
                    setLoading(false);
                })
                .catch(err => {
                    console.error("Worker verisi çekme hatası:", err);
                    setError("Kullanıcı verisi alınamadı.");
                    setLoading(false);
                });

        } catch (err) {
            console.error("Token doğrulama hatası:", err);
            setError("Geçersiz veya süresi dolmuş token. Lütfen tekrar giriş yapın.");
            localStorage.clear();
            setLoading(false);
            navigate("/login");
        }
    }, [token, navigate]);

    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
    };

    if (loading) {
        return (
            <div style={{ padding: "20px", textAlign: "center", fontSize: "1.2em" }}>
                <p>Yönetici Paneli Yükleniyor...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ padding: "20px", color: "red", textAlign: "center" }}>
                <h2>{error}</h2>
                <button
                    onClick={handleLogout}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#007bff",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        marginTop: "15px"
                    }}
                >
                    Giriş Sayfasına Dön
                </button>
            </div>
        );
    }

    // Eğer worker verisi gelmediyse ad soyad yerine kullanıcı adı gösterilebilir
    const displayName = worker ? worker.name : username;

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "1200px", margin: "0 auto" }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "30px", borderBottom: "1px solid #eee", paddingBottom: "20px" }}>
                {/* worker'dan gelen adı göster, yoksa username'i göster */}
                <h1 style={{ color: "#333", margin: 0 }}>👋 Yönetici Paneline Hoşgeldiniz, {displayName}!</h1>
                <button
                    onClick={handleLogout}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#dc3545",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer"
                    }}
                >
                    Çıkış Yap
                </button>
            </div>

            {/* ... diğer JSX kodları */}
            <div style={{ marginBottom: "30px", display: "flex", gap: "15px" }}>
                <button
                    onClick={() => setActiveAdminSection("projects")}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: activeAdminSection === "projects" ? "#007bff" : "#6c757d",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        fontSize: "1em"
                    }}
                >
                    Projeleri Yönet
                </button>
                <button
                    onClick={() => setActiveAdminSection("workers")}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: activeAdminSection === "workers" ? "#007bff" : "#6c757d",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        fontSize: "1em"
                    }}
                >
                    Çalışanları Yönet
                </button>
                <button
                    onClick={() => setActiveAdminSection("assignProject")}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: activeAdminSection === "assignProject" ? "#007bff" : "#6c757d",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        fontSize: "1em"
                    }}
                >
                    Çalışana Proje Ata
                </button>
            </div>
            <div style={{ border: "1px solid #ddd", padding: "20px", borderRadius: "8px", boxShadow: "0 2px 8px rgba(0,0,0,0.05)" }}>
                {activeAdminSection === "projects" && (
                    <section>
                        <h2>📋 Proje Yönetimi</h2>
                        <ProjectList />
                    </section>
                )}
                {activeAdminSection === "workers" && (
                    <section>
                        <h2>👥 Çalışan Yönetimi</h2>
                        <WorkersList />
                    </section>
                )}
                {activeAdminSection === "assignProject" && (
                    <section>
                        <h2>🔗 Çalışana Proje Atama</h2>
                        <ProjectAssignmentForm />
                    </section>
                )}
            </div>
        </div>
    );
}

export default AdminDashboard;
