import React, { useState, useEffect } from "react";

function WorkerForm({ onSubmit, initialData, onCancel }) {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [gender, setGender] = useState("");

    useEffect(() => {
        if (initialData) {
            setName(initialData.name || "");
            setAge(initialData.age !== undefined ? initialData.age : "");
            setGender(initialData.gender || "");
        } else {
            clearForm();
        }
    }, [initialData]);

    const clearForm = () => {
        setName("");
        setAge("");
        setGender("");
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // age'yi sayıya çevir (backend integer bekliyorsa)
        const ageNumber = Number(age);

        onSubmit({
            id: initialData ? initialData.id : undefined,
            name: name.trim(),
            age: ageNumber,
            gender,
        });

        if (!initialData) {
            clearForm();  // Yeni ekleme sonrası formu temizle
        }
        // Eğer güncelleme ise temizlemeyi dışarıda yapmak daha iyi olabilir
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>
                    Ad:
                    <input
                        type="text"
                        placeholder="Ad"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </label>
            </div>
            <div>
                <label>
                    Yaş:
                    <input
                        type="number"
                        placeholder="Yaş"
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                        required
                        min={18}
                        max={100}
                    />
                </label>
            </div>
            <div>
                <label>
                    Cinsiyet:
                    <select
                        value={gender}
                        onChange={(e) => setGender(e.target.value)}
                        required
                    >
                        <option value="">Cinsiyet Seçin</option>
                        <option value="men">Erkek</option>
                        <option value="women">Kadın</option>
                        <option value="other">Diğer</option>
                    </select>
                </label>
            </div>
            <div style={{ marginTop: "10px" }}>
                <button type="submit">{initialData ? "Güncelle" : "Ekle"}</button>
                {initialData && onCancel && (
                    <button
                        type="button"
                        onClick={() => {
                            clearForm();
                            onCancel();
                        }}
                        style={{ marginLeft: "10px" }}
                    >
                        İptal
                    </button>
                )}
            </div>
        </form>
    );
}

export default WorkerForm;
